drop owned by fleet;

create table plane (
    id serial primary key ,
    license_plate varchar(10) unique,
    img bytea
);

create table odometer (
  id serial primary key,
  plane_id integer references plane ( id ),
  date date not null default current_date,
  starts double precision not null check ( starts >= 0 ),
  ends double precision not null check ( ends >= 0 )
);

CREATE TABLE assurance(
    id serial primary key,
    plane_id integer references plane(id),
    debut date not null default current_date,
    fin date not null check(fin>debut)
);

create or replace view v_current_odo as
select
    o.plane_id,
    max(o.ends) ends
from odometer o
group by o.plane_id;


