create table connected (
   id serial primary key,
   token varchar(40) not null unique,
   user_id integer references users ( id ),
   expirationDate date not null default current_date + interval '1 day',
   status boolean not null default true
);

--view assurance
CREATE OR REPLACE VIEW v_assurance AS SELECT a.*,EXTRACT('month' FROM AGE(fin,current_date)) mois  FROM assurance a;


create or replace view v_plane as
select
    v.*,
    vco.ends as current_odo,
    va.debut as begin_assurance,
    va.fin as end_assurance
from plane v
         left join v_current_odo vco on v.id = vco.plane_id
        left join v_assurance va on v.id = va.plane_id;
